import React from "react";
import picture1 from './picture1.jpg';
import picture2 from './picture2.jpg'

var bent = require('bent'); // "Request" library
const client_id = 'CLIENT_ID'; // Your client id
const client_secret = 'CLIENT_SECRET'; // Your secret

 // your application requests authorization
 var authOptions = {
    url: 'http://localhost:3000/webapp',
    headers: {
      'Authorization': 'Basic ' + (new Buffer(client_id + ':' + client_secret).toString('base64'))
    },
    form: {
      grant_type: 'client_credentials'
    },
    json: true
  };

  bent.post(authOptions, function(error, response, body) {
    if (!error && response.statusCode === 200) {

      // use the access token to access the Spotify Web API
      var token = body.access_token;
      var options = {
        url: 'http://localhost:3000/webapp',
        headers: {
          'Authorization': 'Bearer ' + token
        },
        json: true
      };
      bent.get(options, function(error, response, body) {
        console.log(body);
      });
    }
  });


class MainComponent extends React.Component {

    render() {
        return (
        <div>
            <h1>Guessing Game</h1>
            <h2>Based On Spotify's Information Which Album, Track, or Artist is more popular?</h2>
            <img style={{"width" : "20rem"}} src={picture1} alt="logo" />
            <img style={{"width" : "20rem"}}src={picture2} alt="logo" />
        </div>
        )}
}

export default MainComponent
