import React from "react";
import picture1 from './picture1.jpg';
import picture2 from './picture2.jpg';
import { Buffer } from 'buffer';

var client_id = 'f25a526cf53d42c7b794db522c42ab98';
var client_secret = 'c02064daeda847dc99b9ebd7024e8f5d';

// Login Page
export const authEndpoint = 'https://accounts.spotify.com/authorize'
// Spotify Redirect after logging in
const redirectUri = 'http://localhost:3000/'

// Users information
const scopes = [
    'user-read-currently-playing',
    'user-read-recently-played',
    'user-read-playback-state',
    'user-top-read'
]


//Goes through scopes 
// export const loginUrl = `${authEndpoint}?client_id=${client_id}&redirect_uri=${redirectUri}&scope=${scopes.join(
//     '%20'
//     )}&response_type=token&show_dialog=true`;

const loginUrl = `${authEndpoint}?client_id=${client_id}&redirect_uri=${redirectUri}&scope=${scopes.join(
    "%20"
  )}&response_type=token&show_dialog=true`;

class MainComponent extends React.Component {

    render() {
        return (
        <div>
            <button onclick="loginUrl()">Login</button>
            <h1>Guessing Game</h1>
            <h2>Based On Spotify's Information Which Album, Track, or Artist is more popular?</h2>
            <img style={{"width" : "20rem"}} src={picture1} alt="logo" />
            <img style={{"width" : "20rem"}}src={picture2} alt="logo" />
        </div>
        )}
}

export default MainComponent
